import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./signup.css";
import signupimage from "../assets/Group1.png"; // Your signup image

function Signup() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    if (!name || !email || !password) {
      alert("Please fill all fields");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/auth/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password }),
      });

      const data = await res.json();

      if (res.status === 201) {
        alert("Signup successful ✅");

        // Save token & userId for protected routes
        localStorage.setItem("token", data.token);
        localStorage.setItem("userId", data.user._id);

        navigate("/dashboard"); // redirect to dashboard
      } else {
        alert(data.message || "Signup failed ❌");
      }
    } catch (err) {
      console.error(err);
      alert("Something went wrong!");
    }
  };

  return (
    <div className="containersignup">
      <div className="containersignup1">
        <div className="signup-form">
          <div className="form">
            <h2>Create Account</h2>

            <form onSubmit={handleSignup}>
              <input
                type="text"
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              
              <input
                type="email"
                placeholder="Email Address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              
              <button type="submit">Sign Up</button>
            </form>

            <p>
              Already have an account? <Link to="/login">Login</Link>
            </p>
          </div>
        </div>
      </div>

      <div className="containersignup2">
        <img className="img-signup" src={signupimage} alt="signup" />
      </div>
    </div>
  );
}

export default Signup;
