import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserPlus, faUser } from "@fortawesome/free-solid-svg-icons";
import "./App.css";

export default function App() {
  const [tasks, setTasks] = useState([
    { id: 1, title: "Design Landing Page", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "todo" },
    { id: 2, title: "Setup Database Schema", person: "Mavia", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "todo" },
    { id: 3, title: "API Integration", person: "Sameer", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "inprogress" },
    { id: 4, title: "UI Review", person: "Mavia", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "underreview" },
    { id: 5, title: "Deploy to Server", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "done" },
  ]);

  const [title, setTitle] = useState("");
  const [person, setPerson] = useState("");
  const [filterName, setFilterName] = useState("");

  // Add task
  const addTask = () => {
    if (!title.trim() || !person.trim()) return;
    const newTask = {
      id: Date.now(),
      title,
      person,
      photo: "https://randomuser.me/api/portraits/lego/1.jpg",
      status: "todo",
    };
    setTasks([...tasks, newTask]);
    setTitle("");
    setPerson("");
  };

  // Filter tasks by assigned person
  const filteredTasks = tasks.filter(task =>
    filterName.trim() === "" ? true : task.person.toLowerCase().includes(filterName.toLowerCase())
  );

  return (
    <div className="container">
      {/* Header */}
      <header className="header">
        <h1>Simple Kanban</h1>
        <input
          type="text"
          placeholder="Filter by name..."
          value={filterName}
          onChange={(e) => setFilterName(e.target.value)}
        />
      </header>

      {/* Add Task */}
      <div className="input-row">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Assigned To"
          value={person}
          onChange={(e) => setPerson(e.target.value)}
        />
        <button onClick={addTask}>
          <FontAwesomeIcon icon={faUserPlus} /> Add
        </button>
      </div>

      {/* Task List */}
      <div className="tasks-list">
        {filteredTasks.map(task => (
          <div key={task.id} className="task">
            {task.title}
            <div className="person-info">
              <img src={task.photo} alt={task.person} className="person-photo" />
              <FontAwesomeIcon icon={faUser} />
              <span>{task.person}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
