import { useState } from "react";
import "./App.css";

export default function App() {
  const [tasks, setTasks] = useState([
    { id: 1, title: "Design Landing Page", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "todo" },
    { id: 2, title: "Setup Database Schema", person: "Mavia", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "todo" },
    { id: 3, title: "API Integration", person: "Sameer", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "inprogress" },
    { id: 4, title: "Write Unit Tests", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "inprogress" },
    { id: 5, title: "UI Review", person: "Mavia", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "underreview" },
    { id: 6, title: "Fix CSS Bugs", person: "Sameer", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "underreview" },
    { id: 7, title: "Deploy to Server", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "done" },
    { id: 8, title: "Update Documentation", person: "Mavia", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "done" },
    { id: 9, title: "Add Dark Mode", person: "Sameer", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "todo" },
    { id: 10, title: "Optimize Images", person: "Shadulla", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "inprogress" }
  ]);

  const [title, setTitle] = useState("");
  const [person, setPerson] = useState("");
  const [filter, setFilter] = useState("all");

  const addTask = () => {
    if (!title.trim() || !person.trim()) return;
    const newTask = {
      id: Date.now(),
      title,
      person,
      photo: "https://randomuser.me/api/portraits/lego/1.jpg", // default photo for new tasks
      status: "todo",
    };
    setTasks([...tasks, newTask]);
    setTitle("");
    setPerson("");
  };

  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  const handleDrop = (e, newStatus) => {
    const taskId = Number(e.dataTransfer.getData("taskId"));
    setTasks((prev) =>
      prev.map((task) =>
        task.id === taskId ? { ...task, status: newStatus } : task
      )
    );
  };

  const filterTasks = (list) => {
    if (filter === "all") return list;
    return list.filter((t) => t.status === filter);
  };

  return (
    <>
      <header className="header">
        <h1>Kanban Task Manager</h1>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All Tasks</option>
          <option value="todo">Todo</option>
          <option value="inprogress">In Progress</option>
          <option value="underreview">Under Review</option>
          <option value="done">Done</option>
        </select>
      </header>

    <div className="container">

      <div className="input-row">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Assigned To"
          value={person}
          onChange={(e) => setPerson(e.target.value)}
        />
        <button onClick={addTask}>Add</button>
      </div>

      <div className="board">
        {["todo", "inprogress", "underreview", "done"].map((col) => (
          <div
            key={col}
            className="column"
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => handleDrop(e, col)}
          >
            <h2>
              {col === "todo"
                ? "Todo"
                : col === "inprogress"
                ? "In Progress"
                : col === "underreview"
                ? "Under Review"
                : "Done"}
            </h2>

            <div className="tasks">
              {filterTasks(tasks)
                .filter((task) => task.status === col)
                .map((task) => (
                  <div
                    key={task.id}
                    className="task"
                    draggable
                    onDragStart={(e) => handleDragStart(e, task.id)}
                  >
                    <strong>{task.title}</strong>
                    <div className="person-info">
                      <img
                        src={task.photo}
                        alt={task.person}
                        className="person-photo"
                      />
                      <span>{task.person}</span>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
        </>
  );
}
