import { useState } from "react";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { 
  faTasks, 
  faSpinner, 
  faCheckCircle, 
  faEye, 
  faUserPlus, 
  faUser 
} from "@fortawesome/free-solid-svg-icons";

export default function App() {
  const [tasks, setTasks] = useState([
    { id: 1, title: "Design Landing Page", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "todo" },
    { id: 2, title: "Setup Database Schema", person: "Mavia", priority: "Medium", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "todo" },
    { id: 3, title: "API Integration", person: "Sameer", priority: "Low", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "inprogress" },
    { id: 4, title: "Write Unit Tests", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "inprogress" },
    { id: 5, title: "UI Review", person: "Mavia", priority: "Medium", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "underreview" },
    { id: 6, title: "Fix CSS Bugs", person: "Sameer", priority: "Low", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "underreview" },
    { id: 7, title: "Deploy to Server", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "done" },
    { id: 8, title: "Update Documentation", person: "Mavia", priority: "Low", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "done" },
  ]);

  const [title, setTitle] = useState("");
  const [person, setPerson] = useState("");
  const [priority, setPriority] = useState("Medium");
  const [filterStatus, setFilterStatus] = useState("all");
  const [filterName, setFilterName] = useState("");

  // Add task
  const addTask = () => {
    if (!title.trim() || !person.trim()) return;
    const newTask = {
      id: Date.now(),
      title,
      person,
      priority,
      photo: "https://randomuser.me/api/portraits/lego/1.jpg",
      status: "todo",
    };
    setTasks([...tasks, newTask]);
    setTitle("");
    setPerson("");
  };

  // Drag start
  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  // Drop into column
  const handleDrop = (e, newStatus) => {
    const taskId = Number(e.dataTransfer.getData("taskId"));
    setTasks((prev) =>
      prev.map((task) =>
        task.id === taskId ? { ...task, status: newStatus } : task
      )
    );
  };

  // Filter tasks
  const filterTasks = (list) => {
    let filtered = list;
    if (filterName.trim() !== "") {
      filtered = filtered.filter(task =>
        task.person.toLowerCase().includes(filterName.toLowerCase())
      );
    } else if (filterStatus !== "all") {
      filtered = filtered.filter(task => task.status === filterStatus);
    }
    return filtered;
  };

  return (
    <div className="container">
      {/* Header */}
      <header className="header">
        <h1>Kanban Task Manager</h1>
        <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
          <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)}>
            <option value="all">All Tasks</option>
            <option value="todo">Todo</option>
            <option value="inprogress">In Progress</option>
            <option value="underreview">Under Review</option>
            <option value="done">Done</option>
          </select>
          <input
            type="text"
            placeholder="Filter by name..."
            value={filterName}
            onChange={(e) => setFilterName(e.target.value)}
            style={{ padding: "8px 12px", borderRadius: "8px", border: "1px solid #ccc" }}
          />
        </div>
      </header>

      {/* Input Row */}
      <div className="input-row">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Assigned To"
          value={person}
          onChange={(e) => setPerson(e.target.value)}
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <button onClick={addTask}>
          <FontAwesomeIcon icon={faUserPlus} style={{ marginRight: "6px" }} /> Add
        </button>
      </div>

      {/* Kanban Board */}
      <div className="board">
        {["todo", "inprogress", "underreview", "done"].map(col => (
          <div
            key={col}
            className="column"
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => handleDrop(e, col)}
          >
            <h2>
              {col === "todo" ? <> <FontAwesomeIcon icon={faTasks} /> Todo </> :
               col === "inprogress" ? <> <FontAwesomeIcon icon={faSpinner} spin /> In Progress </> :
               col === "underreview" ? <> <FontAwesomeIcon icon={faEye} /> Under Review </> :
               <> <FontAwesomeIcon icon={faCheckCircle} /> Done </>}
            </h2>

            <div className="tasks">
              {filterTasks(tasks)
                .filter(task => task.status === col)
                .map(task => (
                  <div key={task.id} className="task" draggable onDragStart={(e) => handleDragStart(e, task.id)}>
                    {/* Priority Badge */}
                    <span className={`priority ${task.priority.toLowerCase()}`}>{task.priority}</span>

                    <strong>{task.title}</strong>
                    <div className="person-info">
                      <img src={task.photo} alt={task.person} className="person-photo" />
                      <FontAwesomeIcon icon={faUser} style={{ color: "#555", fontSize: "12px" }} />
                      <span>{task.person}</span>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
