import { useEffect, useState } from "react";
import ".A"
import { 
  ListTodo,
  Loader2,
  Eye,
  CheckCircle2,
  UserPlus,
  Filter,
  Search,
  Calendar,
  Users,
  Tag,
  BarChart3,
  Clock,
  TrendingUp,
  AlertCircle,
  CheckSquare,
  XCircle
} from "lucide-react";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [filterStatus, setFilterStatus] = useState("all");
  const [filterName, setFilterName] = useState("");
  const [filterPriority, setFilterPriority] = useState("all");

  // Load tasks from API (simulated with sample data)
  useEffect(() => {
    // Simulated API call - replace with actual API.get("/tasks")
    const sampleTasks = [
      {
        _id: "1",
        title: "Create Blog Content",
        description: "Write a blog about your design process and get it ready for publishing",
        person: "Elise",
        photo: "https://i.pravatar.cc/150?img=1",
        status: "todo",
        priority: "Low"
      },
      {
        _id: "2",
        title: "Send Email to John",
        description: "Make sure to let John know about the issues changed that were made and get his approval",
        person: "Joe",
        photo: "https://i.pravatar.cc/150?img=2",
        status: "todo",
        priority: "High"
      },
      {
        _id: "3",
        title: "Review UX Change",
        description: "Look through the document with the details of the changes that Scott made",
        person: "Noah",
        photo: "https://i.pravatar.cc/150?img=3",
        status: "inprogress",
        priority: "High"
      },
      {
        _id: "4",
        title: "Send Out Proposals",
        description: "Feel free to expand and them a detailed proposal",
        person: "Ethan",
        photo: "https://i.pravatar.cc/150?img=4",
        status: "inprogress",
        priority: "Medium"
      },
      {
        _id: "5",
        title: "Design the Dashboard",
        description: "Be sure to look at client's specs and implement them into the design",
        person: "King",
        photo: "https://i.pravatar.cc/150?img=5",
        status: "underreview",
        priority: "Low"
      },
      {
        _id: "6",
        title: "Send Files to Developer",
        description: "Upload file to Invision before sending over to the dev team",
        person: "Emma",
        photo: "https://i.pravatar.cc/150?img=6",
        status: "done",
        priority: "Medium"
      }
    ];
    setTasks(sampleTasks);
  }, []);

  // Add Task - Simple prompt version (replace with SweetAlert2 in production)
  const handleAddTask = async () => {
    const title = prompt("Task Title:");
    if (!title) return;

    const description = prompt("Description:");
    const person = prompt("Assigned To:");
    if (!person) {
      alert("Please enter person name");
      return;
    }

    const priority = prompt("Priority (High/Medium/Low):") || "Medium";

    try {
      const newTask = {
        _id: Date.now().toString(),
        title: title,
        description: description || "",
        person: person,
        photo: `https://i.pravatar.cc/150?img=${Math.floor(Math.random() * 70)}`,
        status: "todo",
        priority: priority
      };

      setTasks(prev => [...prev, newTask]);
      alert("Task added successfully!");
    } catch (err) {
      console.error(err);
      alert("Could not add task");
    }
  };

  // Drag Start
  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  // Drop
  const handleDrop = async (e, newStatus) => {
    e.preventDefault();
    const taskId = e.dataTransfer.getData("taskId");
    if (!taskId) return;

    try {
      const task = tasks.find(t => t._id === taskId || t.id === taskId);
      if (!task) return;

      setTasks(prev => prev.map(t => (t._id === taskId || t.id === taskId) ? { ...t, status: newStatus } : t));
    } catch (err) {
      console.error(err);
    }
  };

  // Filter tasks by status, name, and priority
  const filterTasks = (list) => {
    return list.filter(task => 
      (filterStatus === "all" || task.status === filterStatus) &&
      (filterPriority === "all" || task.priority.toLowerCase() === filterPriority.toLowerCase()) &&
      (filterName.trim() === "" || task.person.toLowerCase().includes(filterName.toLowerCase()))
    );
  };

  // Calculate statistics
  const getStats = () => {
    const total = tasks.length;
    const todo = tasks.filter(t => t.status === "todo").length;
    const inProgress = tasks.filter(t => t.status === "inprogress").length;
    const review = tasks.filter(t => t.status === "underreview").length;
    const done = tasks.filter(t => t.status === "done").length;
    const highPriority = tasks.filter(t => t.priority === "High").length;

    return { total, todo, inProgress, review, done, highPriority };
  };

  const stats = getStats();

  return (
    <div className="container">
      <div className="container2">
        <div className="sidebar">
          <div className="sidebar-inside">
            {/* Filters Header */}
            <div className="sidebar-header">
              <Filter size={18} />
              <h3>Filters & Overview</h3>
            </div>

            {/* Statistics Section */}
            <div className="stats-section">
              <div className="stat-item">
                <div className="stat-icon total">
                  <BarChart3 size={16} />
                </div>
                <div className="stat-content">
                  <span className="stat-label">Total Tasks</span>
                  <span className="stat-value">{stats.total}</span>
                </div>
              </div>

              <div className="stat-item">
                <div className="stat-icon high">
                  <AlertCircle size={16} />
                </div>
                <div className="stat-content">
                  <span className="stat-label">High Priority</span>
                  <span className="stat-value">{stats.highPriority}</span>
                </div>
              </div>

              <div className="stat-item">
                <div className="stat-icon done">
                  <CheckSquare size={16} />
                </div>
                <div className="stat-content">
                  <span className="stat-label">Completed</span>
                  <span className="stat-value">{stats.done}</span>
                </div>
              </div>
            </div>

            {/* Filter by Status */}
            <div className="filter-group">
              <label>
                <ListTodo size={14} />
                Status
              </label>
              <div className="select-wrapper">
                <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)}>
                  <option value="all">All Tasks</option>
                  <option value="todo">üìã Todo</option>
                  <option value="inprogress">‚ö° In Progress</option>
                  <option value="underreview">üëÅÔ∏è Under Review</option>
                  <option value="done">‚úÖ Done</option>
                </select>
              </div>
            </div>

            {/* Filter by Priority */}
            <div className="filter-group">
              <label>
                <Tag size={14} />
                Priority
              </label>
              <div className="select-wrapper">
                <select value={filterPriority} onChange={(e) => setFilterPriority(e.target.value)}>
                  <option value="all">All Priorities</option>
                  <option value="High">üî¥ High</option>
                  <option value="Medium">üü° Medium</option>
                  <option value="Low">üü¢ Low</option>
                </select>
              </div>
            </div>

            {/* Filter by Name */}
            <div className="filter-group">
              <label>
                <Users size={14} />
                Assigned To
              </label>
              <div className="input-wrapper">
                <Search size={14} className="input-icon" />
                <input 
                  type="text" 
                  placeholder="Search by name..." 
                  value={filterName} 
                  onChange={(e) => setFilterName(e.target.value)} 
                />
              </div>
            </div>

            {/* Quick Stats */}
            <div className="quick-stats">
              <div className="quick-stat-row">
                <span className="quick-stat-label">
                  <Clock size={12} />
                  In Progress
                </span>
                <span className="quick-stat-value">{stats.inProgress}</span>
              </div>
              <div className="quick-stat-row">
                <span className="quick-stat-label">
                  <Eye size={12} />
                  Under Review
                </span>
                <span className="quick-stat-value">{stats.review}</span>
              </div>
              <div className="quick-stat-row">
                <span className="quick-stat-label">
                  <TrendingUp size={12} />
                  Completion Rate
                </span>
                <span className="quick-stat-value">
                  {stats.total > 0 ? Math.round((stats.done / stats.total) * 100) : 0}%
                </span>
              </div>
            </div>

            {/* Reset Button */}
            <button 
              className="reset-btn"
              onClick={() => {
                setFilterStatus("all");
                setFilterPriority("all");
                setFilterName("");
              }}
            >
              <XCircle size={16} />
              Reset All Filters
            </button>
          </div>
        </div>

        <div className="board">
          <header className="header">
            <h1>Kanban Task Manager</h1>
            <button className="add-btn" onClick={handleAddTask}>
              <UserPlus size={18} /> Add Task
            </button>
          </header>

          {["todo", "inprogress", "underreview", "done"].map(col => (
            <div
              key={col}
              className="column"
              onDragOver={(e) => e.preventDefault()}
              onDrop={(e) => handleDrop(e, col)}
            >
              <h2>
                {col === "todo" ? <><ListTodo size={18} /> Todo</> :
                 col === "inprogress" ? <><Loader2 size={18} className="spin" /> In Progress</> :
                 col === "underreview" ? <><Eye size={18} /> Under Review</> :
                 <><CheckCircle2 size={18} /> Done</>}
                <span className="column-count">{filterTasks(tasks).filter(t => t.status === col).length}</span>
              </h2>

              <div className="tasks">
                {filterTasks(tasks)
                  .filter(task => task.status === col)
                  .map(task => (
                    <div 
                      key={task._id || task.id} 
                      className="task" 
                      draggable 
                      onDragStart={(e) => handleDragStart(e, task._id || task.id)}
                    >
                      <div className="task-top">
                        <h4>{task.title}</h4> 
                        <p>{task.description}</p>
                        <span className={`priority ${task.priority}`}>{task.priority}</span>
                      </div>

                      <div className="person-info">
                        <img src={task.photo} alt={task.person} className="person-photo" />
                        <span>{task.person}</span>
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
