import { useEffect, useState } from "react";
import Swal from "sweetalert2";
import "./App.css";
import API from "./api";

// Lucide Icons
import { ListTodo, Loader, Eye, CheckCircle2, UserPlus } from "lucide-react";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [filterStatus, setFilterStatus] = useState("all");
  const [filterName, setFilterName] = useState("");
  const [filterPriority, setFilterPriority] = useState("all");

  // Load tasks from API
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await API.get("/tasks");
        setTasks(
          res.data.map((t) => ({
            ...t,
            showMenu: false, // for three-dot menu
          }))
        );
      } catch (err) {
        console.error(err);
      }
    };
    fetchTasks();
  }, []);

  // Count tasks by status
  const countTasks = (status) => tasks.filter((t) => t.status === status).length;

  // Add Task
  const handleAddTask = async () => {
    const { value: formValues } = await Swal.fire({
      title: "Add New Task",
      html:
        '<input id="swal-task-title" class="swal2-input" placeholder="Task Title">' +
        '<input id="swal-task-description" class="swal2-input" placeholder="Description">' +
        '<input id="swal-task-person" class="swal2-input" placeholder="Assigned To">' +
        '<select id="swal-task-priority" class="swal2-select">' +
        '<option value="High">High</option>' +
        '<option value="Medium">Medium</option>' +
        '<option value="Low">Low</option>' +
        "</select>",
      focusConfirm: false,
      showCancelButton: true,
      confirmButtonText: "Add Task",
      cancelButtonText: "Cancel",
      preConfirm: () => {
        const title = document.getElementById("swal-task-title").value;
        const description = document.getElementById("swal-task-description").value;
        const person = document.getElementById("swal-task-person").value;
        const priority = document.getElementById("swal-task-priority").value;

        if (!title || !person) {
          Swal.showValidationMessage("Please enter both title and person");
          return false;
        }
        return { title, description, person, priority };
      },
    });

    if (formValues) {
      try {
        const newTask = {
          title: formValues.title,
          description: formValues.description,
          person: formValues.person,
          photo: "https://randomuser.me/api/portraits/lego/1.jpg",
          status: "todo",
          priority: formValues.priority,
          showMenu: false,
        };

        const res = await API.post("/tasks", newTask);
        setTasks((prev) => [...prev, res.data]);
        Swal.fire("Added!", "Task added successfully", "success");
      } catch (err) {
        console.error(err);
        Swal.fire("Error", "Could not add task", "error");
      }
    }
  };

  // Drag Start
  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  // Drop Task
  const handleDrop = async (e, newStatus) => {
    e.preventDefault();
    const taskId = e.dataTransfer.getData("taskId");
    if (!taskId) return;

    try {
      const task = tasks.find((t) => t._id === taskId || t.id === taskId);
      if (!task) return;

      const updated = { ...task, status: newStatus };
      await API.put(`/tasks/${taskId}`, updated);

      setTasks((prev) =>
        prev.map((t) =>
          t._id === taskId || t.id === taskId ? { ...t, status: newStatus } : t
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  // Filters
  const filterTasks = (list) => {
    return list.filter(
      (task) =>
        (filterStatus === "all" || task.status === filterStatus) &&
        (filterPriority === "all" ||
          task.priority.toLowerCase() === filterPriority.toLowerCase()) &&
        (filterName.trim() === "" ||
          task.person.toLowerCase().includes(filterName.toLowerCase()))
    );
  };

  // Toggle three-dot menu for a task
  const toggleMenu = (taskId) => {
    setTasks((prev) =>
      prev.map((t) =>
        t._id === taskId || t.id === taskId
          ? { ...t, showMenu: !t.showMenu }
          : t
      )
    );
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex">
        {/* Sidebar */}
        <div className="w-1/4 p-4 border-r">
          <h2 className="font-bold mb-4">Tasks</h2>
          <div className="space-y-2 mb-4">
            <p>
              <ListTodo size={18} /> Todo {countTasks("todo")}
            </p>
            <p>
              <Loader size={18} className="animate-spin" /> In Progress{" "}
              {countTasks("inprogress")}
            </p>
            <p>
              <Eye size={18} /> Under Review {countTasks("underreview")}
            </p>
            <p>
              <CheckCircle2 size={18} /> Done {countTasks("done")}
            </p>
          </div>

          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="w-full mb-2 border rounded p-1"
          >
            <option value="all">All Tasks</option>
            <option value="todo">Todo</option>
            <option value="inprogress">In Progress</option>
            <option value="underreview">Under Review</option>
            <option value="done">Done</option>
          </select>

          <select
            value={filterPriority}
            onChange={(e) => setFilterPriority(e.target.value)}
            className="w-full mb-2 border rounded p-1"
          >
            <option value="all">All Priorities</option>
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>

          <input
            type="text"
            placeholder="Filter by name..."
            value={filterName}
            onChange={(e) => setFilterName(e.target.value)}
            className="w-full border rounded p-1"
          />
        </div>

        {/* Board */}
        <div className="flex-1 p-4">
          <header className="flex justify-between items-center mb-4">
            <h1 className="text-xl font-bold">Kanban Task Manager</h1>
            <button
              className="flex items-center gap-1 bg-blue-500 text-white px-3 py-1 rounded"
              onClick={handleAddTask}
            >
              <UserPlus size={18} /> Add Task
            </button>
          </header>

          <div className="flex gap-4">
            {["todo", "inprogress", "underreview", "done"].map((col) => (
              <div
                key={col}
                className="flex-1 border rounded p-2 bg-gray-50"
                onDragOver={(e) => e.preventDefault()}
                onDrop={(e) => handleDrop(e, col)}
              >
                <h2 className="font-semibold mb-2 text-gray-700">
                  {col === "todo" ? (
                    <>
                      <ListTodo size={18} /> Todo
                    </>
                  ) : col === "inprogress" ? (
                    <>
                      <Loader size={18} className="animate-spin" /> In Progress
                    </>
                  ) : col === "underreview" ? (
                    <>
                      <Eye size={18} /> Under Review
                    </>
                  ) : (
                    <>
                      <CheckCircle2 size={18} /> Done
                    </>
                  )}
                </h2>

                <div className="space-y-2">
                  {filterTasks(tasks)
                    .filter((task) => task.status === col)
                    .map((task) => (
                      <div
                        key={task._id || task.id}
                        className="task bg-white p-3 rounded shadow cursor-grab relative"
                        draggable
                        onDragStart={(e) =>
                          handleDragStart(e, task._id || task.id)
                        }
                      >
                        {/* Task Top */}
                        <div className="task-top relative">
                          <h4 className="font-semibold">{task.title}</h4>
                          <p className="text-gray-600 text-sm">
                            {task.description}
                          </p>

                          {/* Priority */}
                          <span
                            className={`absolute top-0 left-0 px-2 py-0.5 text-xs rounded-full font-bold ${
                              task.priority === "High"
                                ? "bg-red-500 text-white"
                                : task.priority === "Medium"
                                ? "bg-yellow-500 text-white"
                                : "bg-green-500 text-white"
                            }`}
                          >
                            {task.priority}
                          </span>

                          {/* Three-dot menu */}
                          <div className="absolute top-0 right-0">
                            <button
                              className="text-gray-500 hover:text-gray-700 p-1"
                              onClick={() => toggleMenu(task._id)}
                            >
                              â‹®
                            </button>

                            {task.showMenu && (
                              <div className="absolute right-0 mt-2 w-36 bg-white border border-gray-200 rounded-md shadow-lg z-10">
                                <p className="px-4 py-2 text-sm text-gray-600 hover:bg-gray-100 cursor-pointer">
                                  Edit
                                </p>
                                <p className="px-4 py-2 text-sm text-gray-600 hover:bg-gray-100 cursor-pointer">
                                  Delete
                                </p>
                                <p className="px-4 py-2 text-sm text-gray-600 hover:bg-gray-100 cursor-pointer">
                                  View
                                </p>
                              </div>
                            )}
                          </div>
                        </div>

                        {/* Task Lower Section */}
                        <div className="task-lower-sec flex justify-between items-center mt-2 border-t pt-1">
                          <div className="flex items-center gap-2">
                            <img
                              src={task.photo}
                              alt={task.person}
                              className="w-6 h-6 rounded-full object-cover"
                            />
                            <span className="text-gray-600 text-xs">
                              {task.person}
                            </span>
                          </div>
                          <span className="text-gray-500 text-xs">
                            {new Date().toLocaleDateString("en-GB", {
                              day: "2-digit",
                              month: "short",
                              year: "numeric",
                            })}
                          </span>
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
