import { useState } from "react";
import "./App.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { 
  faTasks, 
  faSpinner, 
  faCheckCircle, 
  faEye, 
  faUserPlus, 
  faUser 
} from "@fortawesome/free-solid-svg-icons";

export default function App() {
  const [tasks, setTasks] = useState([
    { id: 1, title: "Design Landing Page", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "todo" },
    { id: 2, title: "Setup Database Schema", person: "Mavia", priority: "Medium", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "todo" },
    { id: 3, title: "API Integration", person: "Sameer", priority: "Low", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "inprogress" },
    { id: 4, title: "Write Unit Tests", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "inprogress" },
    { id: 5, title: "UI Review", person: "Mavia", priority: "Medium", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "underreview" },
    { id: 6, title: "Fix CSS Bugs", person: "Sameer", priority: "Low", photo: "https://randomuser.me/api/portraits/men/54.jpg", status: "underreview" },
    { id: 7, title: "Deploy to Server", person: "Shadulla", priority: "High", photo: "https://randomuser.me/api/portraits/men/32.jpg", status: "done" },
    { id: 8, title: "Update Documentation", person: "Mavia", priority: "Low", photo: "https://randomuser.me/api/portraits/women/44.jpg", status: "done" },
  ]);

  const [title, setTitle] = useState("");
  const [person, setPerson] = useState("");
  const [priority, setPriority] = useState("Medium");
  const [filterName, setFilterName] = useState("");

  // Add new task
  const addTask = () => {
    if (!title.trim() || !person.trim()) return;
    const newTask = {
      id: Date.now(),
      title,
      person,
      priority,
      photo: "https://randomuser.me/api/portraits/lego/1.jpg",
      status: "todo",
    };
    setTasks([...tasks, newTask]);
    setTitle("");
    setPerson("");
  };

  // Drag start
  const handleDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  // Drop into a column
  const handleDrop = (e, newStatus) => {
    const taskId = Number(e.dataTransfer.getData("taskId"));
    setTasks(prev =>
      prev.map(task => (task.id === taskId ? { ...task, status: newStatus } : task))
    );
  };

  // Filter tasks by name
  const filterTasks = tasks.filter(task => 
    filterName.trim() === "" || task.person.toLowerCase().includes(filterName.toLowerCase())
  );

  // Group tasks by status -> person -> priority
  const groupTasks = (tasksList) => {
    const statusGroups = {};
    tasksList.forEach(task => {
      if (!statusGroups[task.status]) statusGroups[task.status] = {};
      if (!statusGroups[task.status][task.person]) statusGroups[task.status][task.person] = {};
      if (!statusGroups[task.status][task.person][task.priority]) statusGroups[task.status][task.person][task.priority] = [];
      statusGroups[task.status][task.person][task.priority].push(task);
    });
    return statusGroups;
  };

  const grouped = groupTasks(filterTasks);

  const statusOrder = ["todo", "inprogress", "underreview", "done"];

  return (
    <div className="container">
      {/* Header */}
      <header className="header">
        <h1>Kanban Task Manager</h1>
        <input
          type="text"
          placeholder="Filter by name..."
          value={filterName}
          onChange={(e) => setFilterName(e.target.value)}
        />
      </header>

      {/* Input Row */}
      <div className="input-row">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Assigned To"
          value={person}
          onChange={(e) => setPerson(e.target.value)}
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        <button onClick={addTask}>
          <FontAwesomeIcon icon={faUserPlus} /> Add
        </button>
      </div>

      {/* Kanban Board */}
      <div className="board">
        {statusOrder.map(status => (
          <div key={status} className="column" onDragOver={e => e.preventDefault()} onDrop={e => handleDrop(e, status)}>
            <h2>
              {status === "todo" ? <><FontAwesomeIcon icon={faTasks} /> Todo</> :
               status === "inprogress" ? <><FontAwesomeIcon icon={faSpinner} spin /> In Progress</> :
               status === "underreview" ? <><FontAwesomeIcon icon={faEye} /> Under Review</> :
               <><FontAwesomeIcon icon={faCheckCircle} /> Done</>}
            </h2>

            <div className="tasks">
              {grouped[status] &&
                Object.keys(grouped[status]).map(user => (
                  <div key={user}>
                    <h4>{user}</h4>
                    {Object.keys(grouped[status][user]).map(priority => (
                      <div key={priority}>
                        <em>{priority} Priority</em>
                        {grouped[status][user][priority].map(task => (
                          <div key={task.id} className="task" draggable onDragStart={(e) => handleDragStart(e, task.id)}>
                            <strong>{task.title}</strong>
                            <div className="person-info">
                              <img src={task.photo} alt={task.person} className="person-photo" />
                              <FontAwesomeIcon icon={faUser} />
                              <span>{task.person}</span>
                            </div>
                          </div>
                        ))}
                      </div>
                    ))}
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
